import { useEffect, useState } from 'react';
import '../style/Obstawianie.css';

function Obstawianie ({ kurs }) {

    const [stawka, setStawka] = useState(null);
    const [wygrana, setWygrana] = useState(0);

    const handleChange = (e) => {
        const stawkaValue = e.target.value;
        setStawka(stawkaValue);
        setWygrana(stawka * kurs);
    }

    return (
        <div className='Obstawianie'>
            <p>Ile pieniędzy chcesz obstawić?</p>
            <input 
                type="text" 
                id="pieniadze" 
                onKeyUp={handleChange}
            /> <br /> 
            <p>Twój kurs: </p>
            <div id="kurs" >{kurs}</div>
            <p>Do wygrania:</p>
            <div id="wygrana">{wygrana} PLN</div>
            <input 
                type="button" 
                value="Obstaw" 
            />
            <div id="podsumowanie"></div>
        </div>
    );
}

export default Obstawianie;
