import { useEffect, useState } from 'react';
import '../style/Obstawianie.css';

function Obstawianie () {
    
    const [pieniadze, setPieniadze] = useState(1);
    const [wygrana, setWygrana] = useState(0);
    const [calkowityKurs, setCalkowityKurs] = useState(1);
    const [podsumowanie, setPodsumowanie] = useState('');

        // Funkcja do obliczania wygranej po zmianie wartości pieniędzy lub kursu
        const obliczWygrana = () => {
            const parsedPieniadze = parseFloat(pieniadze);
            const parsedKurs = parseFloat(calkowityKurs);
            if (!isNaN(parsedPieniadze) && !isNaN(parsedKurs)) {
                const calculatedWygrana = parsedPieniadze * parsedKurs;
                setWygrana(Math.round(calculatedWygrana * 100) / 100);
            }
        };

    const obstaw = () => {
        const nowePodsumowanie = `Obstawiono ${pieniadze}zł z kursem ${calkowityKurs}<br> Możliwa wygrana: ${wygrana}zł.`;
        setPodsumowanie(nowePodsumowanie);
    };

    return (
        <div className='Obstawianie'>
            <p>Ile pieniędzy chcesz obstawić?</p>
            <input 
                type="text" 
                id="pieniadze" 
                value={pieniadze}
                onChange={(e) => {
                    setPieniadze(e.target.value)
                    obliczWygrana()    
                }}
            /> <br /> 
            <p>Twój kurs: </p>
            <div id="kurs">{calkowityKurs}</div>
            <p>Do wygrania:</p>
            <div id="wygrana">{wygrana} PLN</div>
            <input 
                type="button" 
                value="Obstaw" 
                onClick={obstaw}
            />
            <div id="podsumowanie" dangerouslySetInnerHTML={{ __html: podsumowanie }}></div>
        </div>
    );
}

export default Obstawianie;
