import React, { useState } from 'react';
import '../style/Obstawianie.css';

function Obstawianie() {
    const [amount, setAmount] = useState('');
    const [bettingOdds, setBettingOdds] = useState(1);
    const [possibleWin, setPossibleWin] = useState(0);

    const handleBet = (selectedBet) => {
        setBettingOdds(selectedBet);
    };

    const calculatePossibleWin = () => {
        const inputAmount = parseFloat(amount);
        if (!isNaN(inputAmount) && inputAmount > 0 && bettingOdds > 1) {
            const win = inputAmount * bettingOdds;
            setPossibleWin(win.toFixed(2));
        }
    };

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    return (
        <div className="Obstawianie">
            <p>Ile pieniędzy chcesz obstawić?</p>
            <input type="text" id="pieniadze" value={amount} onChange={handleAmountChange} /> <br />
            <p>Twój kurs: {bettingOdds}</p>
            <p>Do wygrania: {possibleWin}</p>
            <button onClick={calculatePossibleWin}>Oblicz wygraną</button>
        </div>
    );
}

export default Obstawianie;
