import React, { useState } from 'react';
import '../style/Fieldset.css';

function Fieldset(props) {
    const [calkowityKurs, setCalkowityKurs] = useState(1);

    const zmienKolor = (id) => {
        let button = document.getElementById(id);
        button.classList.add("clicked");
        button.disabled = true;
    };

    const ustawKurs = (kursDruzyny) => {
        const obecnyKurs = document.querySelector("#kurs").value;
        setCalkowityKurs(obecnyKurs * kursDruzyny);
        document.querySelector("#kurs").innerHTML=calkowityKurs;
    }

    return (
        <fieldset>
            <legend>{props.tytul}</legend>
            {props.druzyna1} <input
                type="button"
                value={props.kurs1}
                id={props.id1}
                onClick={() => {
                    zmienKolor(props.id1);
                    ustawKurs(props.kurs1)
                }}
            />
            <br />
            {props.druzyna2} <input
                type="button"
                value={props.kurs2}
                id={props.id2}
                onClick={() => {
                    zmienKolor(props.id2);
                    ustawKurs(props.kurs2)
                }}
            />
        </fieldset>
    );
}

export default Fieldset;
