import React, { useState } from 'react';
import '../style/Fieldset.css';

function Fieldset(props) {
    const [selectedBet, setSelectedBet] = useState(null);

    const zmienKolor = (id, kurs) => {
        let button = document.getElementById(id);
        button.classList.add("clicked");
        button.disabled = true;
        setSelectedBet(kurs);
    };

    return (
        <fieldset>
            <legend>{props.tytul}</legend>
            {props.druzyna1}{' '}
            <input
                type="button"
                value={props.kurs1}
                id={props.id1}
                onClick={() => {
                    zmienKolor(props.id1, props.kurs1);
                }}
            />
            <br />
            {props.druzyna2}{' '}
            <input
                type="button"
                value={props.kurs2}
                id={props.id2}
                onClick={() => {
                    zmienKolor(props.id2, props.kurs2);
                }}
            />
            {selectedBet && (
                <button
                    onClick={() => {
                        props.onBet(selectedBet);
                    }}
                >
                    Obstaw
                </button>
            )}
        </fieldset>
    );
}

export default Fieldset;
