import React, { useState } from 'react';
import '../style/Fieldset.css';

function Fieldset(props) {
    const [calkowityKurs, setCalkowityKurs] = useState(document.querySelector("#kurs").value);

    const zmienKolor = (id) => {
        let button = document.getElementById(id);
        button.classList.add("clicked");
        button.disabled = true;
    };


    return (
        <fieldset>
            <legend>{props.tytul}</legend>
            {props.druzyna1} <input
                type="button"
                value={props.kurs1}
                id={props.id1}
                onClick={() => {
                    zmienKolor(props.id1);
                }}
            />
            <br />
            {props.druzyna2} <input
                type="button"
                value={props.kurs2}
                id={props.id2}
                onClick={() => {
                    zmienKolor(props.id2);
                }}
            />
        </fieldset>
    );
}

export default Fieldset;
