import Fieldset from "./Fieldset";
import '../style/Mecze.css';
import React, { useState, useEffect } from 'react';
import axios from "axios";

function Mecze() {
    const [matches, setMatches] = useState([]);

    useEffect(() => {
      const fetchMatches = async () => {
        try {
  
          // Pobieranie danych z API
          const response = await axios.get('http://api.football-data.org/v4/matches');
  
          // Przetwarzanie danych i ustawianie ich w stanie komponentu
          setMatches(response.data.matches.slice(0, 5)); // Przykładowo, pobierz pierwsze 5 meczów
        } catch (error) {
          console.error('Error fetching matches:', error);
        }
      };
  
      fetchMatches();
    }, []); // Pusta tablica oznacza, że useEffect zostanie wykonany tylko raz po zamontowaniu komponentu
  
    return (
      <div className="Mecze">
        {matches.map((match) => (
          <Fieldset
            key={match.id}
            tytul="Piłka nożna"
            druzyna1={match.homeTeam.name}
            druzyna2={match.awayTeam.name}
            kurs1="1.25"
            kurs2="2.35"
            id1={`kurs${match.id}.1`}
            id2={`kurs${match.id}.2`}
          />
        ))}
      </div>
    );
  }
  

export default Mecze;


