import React, {useState} from 'react';
import '../style/Fieldset.css'

function Fieldset({ tytul, druzyna1, druzyna2, kurs1, kurs2, id1, id2, aktywnyKurs }) {
    const [selectedId, setSelectedId] = useState(null);

    const handleClick = (clickedId, kurs) => {
        setSelectedId(clickedId);
        aktywnyKurs = kurs;
    };

    return (
        <fieldset>
            <legend>{tytul}</legend>
            {druzyna1} <button
                type="button"
                onClick={() => handleClick(id1, id2, kurs1)}
                disabled={selectedId === id1}
                style={{ backgroundColor: selectedId === id1 ? '#285e2a' : '#4CAF50' }}
            >
                {kurs1}
            </button>
            <br />
            {druzyna2} <button
                type="button"
                onClick={() => handleClick(id2, id1, kurs2)}
                disabled={selectedId === id2}
                style={{ backgroundColor: selectedId === id2 ? '#285e2a' : '#4CAF50' }}
            >
                {kurs2}
            </button>
        </fieldset>
    );
}

export default Fieldset;
